mapper.select:
select a.* from ( select rownum rn, b.* from (
SELECT cl_no ,class_name ,address ,timetable ,totaltime ,class_cd FROM class ORDER BY cl_no
) b ) a where rn between #{first} and #{last}

mapper.insert:
SELECT NVL(MAX(cl_no),0)+1 FROM CLASS
INSERT INTO class( cl_no ,class_name ,address ,timetable ,totaltime ,class_cd
,timetable
) VALUES( #{cl_no}, #{class_name}, #{address}, #{timetable}, #{totaltime}, #{class_cd}
,#{timetable}
)
