mapper.insert:

	SELECT NVL(MAX(c_no),0)+1 FROM CONSULT

	INSERT INTO CONSULT(
							c_no
							,cdate
							,s_detail
							,cl_no
							,title
							,content
							,writer
						   )
	VALUES(
			#{c_no}
			,sysdate
			,#{s_detail}
			,#{cl_no}
			,#{title}
			,#{content}
			,#{writer}
		)

mapper.update:
UPDATE CONSULT SET 
						s_detail=#{s_detail}
						,cl_no=#{cl_no}
						,c_date=sysdate
						,title=#{title}
						,content=#{content}
WHERE C_NO = #{c_no}
